mapper.select:
select
        id as id,
        isbn10 as isbn10,
        title as title,
        subtitle as subtitle,
        pic as pic,
        author as author,
        publisher as publisher,
        pubplace as pubplace,
        pubdate as pubdate,
        page as page,
        price as price,
        binding as binding,
        isbn13 as isbn13,
        keyword as keyword,
        edition as edition,
        impression as impression,
        language as language
from book


            and id=#{id}


            and isbn10=#{isbn10}


            and title=#{title}


            and subtitle=#{subtitle}


            and pic=#{pic}


            and author=#{author}


            and publisher=#{publisher}


            and pubplace=#{pubplace}


            and pubdate=#{pubdate}


            and page=#{page}


            and price=#{price}


            and binding=#{binding}


            and isbn13=#{isbn13}


            and keyword=#{keyword}


            and edition=#{edition}


            and impression=#{impression}


            and language=#{language}



    limit #{beginPos},#{pageSize}

select count(*) from book


            and id=#{id}


            and isbn10=#{isbn10}


            and title=#{title}


            and subtitle=#{subtitle}


            and pic=#{pic}


            and author=#{author}


            and publisher=#{publisher}


            and pubplace=#{pubplace}


            and pubdate=#{pubdate}


            and page=#{page}


            and price=#{price}


            and binding=#{binding}


            and isbn13=#{isbn13}


            and keyword=#{keyword}


            and edition=#{edition}


            and impression=#{impression}


            and language=#{language}
