mapper.select:
select id as id, phone as phone, password as password, wxUserId as wxUserId, realName as realName, nickname as nickName, gender as gender, birthday as birthday, createdTime as createdTime, updatedTime as updatedTime, avatarUrl as avatarUrl
from user
and id=#{id}

select
id as id,
phone as phone,
password as password,
wxUserId as wxUserId,
realName as realName,
nickName as nickName,
gender as gender,
birthday as birthday,
createdTime as createdTime,
updatedTime as updatedTime,
avatarUrl as avatarUrl

from user


        and id=#{id}


        and phone=#{phone}


        and password=#{password}


        and wxUserId=#{wxUserId}


        and realName=#{realName}


        and nickName=#{nickName}


        and gender=#{gender}


        and birthday=#{birthday}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}


        and avatarUrl=#{avatarUrl}



    limit #{beginPos},#{pageSize}

select count(*) from user


        and id=#{id}


        and phone=#{phone}


        and password=#{password}


        and wxUserId=#{wxUserId}


        and realName=#{realName}


        and nickName=#{nickName}


        and gender=#{gender}


        and birthday=#{birthday}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}


        and avatarUrl=#{avatarUrl}
