mapper.select:
select id as id, phone as phone, email as email, wxnum as wxnum, qqnum as qqnum, password as password, realName as realName, nickname as nickName, gender as gender, birthday as birthday, createdTime as createdTime, updatedTime as updatedTime, avatarUrl as avatarUrl
from user
and id=#{id}

select
id as id,
phone as phone,
email as email,
wxnum as wxnum,
qqnum as qqnum,
password as password,
realName as realName,
nickName as nickName,
gender as gender,
birthday as birthday,
createdTime as createdTime,
updatedTime as updatedTime,
avatarUrl as avatarUrl

from user


        and id=#{id}


        and phone=#{phone}


        and email=#{email}


        and wxnum=#{wxnum}


        and qqnum=#{qqnum}


        and password=#{password}


        and realName=#{realName}


        and nickName=#{nickName}


        and gender=#{gender}


        and birthday=#{birthday}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}


        and avatarUrl=#{avatarUrl}



    limit #{beginPos},#{pageSize}

select count(*) from user


        and id=#{id}


        and phone=#{phone}


        and password=#{password}


        and realName=#{realName}


        and nickName=#{nickName}


        and gender=#{gender}


        and birthday=#{birthday}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}


        and avatarUrl=#{avatarUrl}
